mapper.update:
UPDATE users
SET phoneNumber = #{ph}
WHERE account = #{account}

UPDATE users
SET address = #{ad}
WHERE account = #{account}

UPDATE users
SET password = #{pw}
WHERE account = #{account}

UPDATE users
SET is_withdrawn = true
WHERE account = #{account}

UPDATE users
SET remained_point = remained_point + #{amount}
WHERE account = #{account}

UPDATE users
SET remained_point = remained_point - #{amount}
WHERE account = #{account}
